mapper.update:
update card_batch
set pre_warn_day = #{prdDay,jdbcType=INTEGER},warn_time = #{warnTime,jdbcType=VARCHAR},update_time = #{updateTime,jdbcType=VARCHAR}
where batch_id = #{id,jdbcType=VARCHAR}

update card_batch
set ${field} = ${field} + #{num,jdbcType=INTEGER} ,card_num = card_num - #{num,jdbcType=INTEGER},update_time = #{updateTime,jdbcType=VARCHAR}
where batch_id = #{id,jdbcType=VARCHAR}

update card_batch
set  del_num = card_num,card_num = card_num - card_num
where batch_id = #{id,jdbcType=VARCHAR}
