mapper.select:
select 

from pro_images
where spu_id = #{spuId,jdbcType=INTEGER}
  and pro_prov_id = #{proProvId,jdbcType=INTEGER}

mapper.delete:
delete from pro_images
where spu_id = #{spuId,jdbcType=INTEGER}
  and pro_prov_id = #{proProvId,jdbcType=INTEGER}

mapper.update:
update pro_images
set pro_prop_id = #{proPropId,jdbcType=INTEGER},
  default_image_uri = #{defaultImageUri,jdbcType=VARCHAR},
  image_uris = #{imageUris,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where spu_id = #{spuId,jdbcType=INTEGER}
  and pro_prov_id = #{proProvId,jdbcType=INTEGER}

update pro_images
  set default_image_uri = #{primaryImageUrl,jdbcType=VARCHAR},
  update_time =#{nowTime,jdbcType=TIMESTAMP}
where spu_id = #{spuId,jdbcType=INTEGER}
and pro_prop_id = #{proPid,jdbcType=INTEGER}
and pro_prov_id = #{proVid,jdbcType=INTEGER}
