  admin_pwd, admin_phone, department, 
  admin_email, start_time, end_time, 
  state, create_time, update_time ,finance_pwd
  )
values (#{adminId,jdbcType=INTEGER}, #{loginName,jdbcType=VARCHAR}, #{adminName,jdbcType=VARCHAR}, 
  #{adminPwd,jdbcType=VARCHAR}, #{adminPhone,jdbcType=VARCHAR}, #{department,jdbcType=VARCHAR}, 
  #{adminEmail,jdbcType=VARCHAR}, #{startTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, 
  #{state,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{financePwd,jdbcType=VARCHAR}
  )

trim.if:

    and admin_id=#{entity.adminId,jdbcType=INTEGER}


    and login_name= #{entity.loginName,jdbcType=VARCHAR}


    and state= #{entity.state,jdbcType=INTEGER}



    and ( admin_name like #{entity.adminName,jdbcType=CHAR}
           or login_name like #{entity.adminName,jdbcType=CHAR}
           or department like #{entity.adminName,jdbcType=CHAR}
           or admin_phone like #{entity.adminName,jdbcType=CHAR}
           or admin_email like #{entity.adminName,jdbcType=CHAR}
         )

and is_del = 0

mapper.update:
update bks_admin
